import React,{Component} from 'react';
import * as actions from './action/index';
import Header from './widgets/header';




class DetailsAlbum extends Component {
    state = {
        album : '',
        tracks : []
    }
    componentDidMount(){
        actions.getAlbum(this.props.match.params .id).then( album => {
            this.setState({
                album,
                tracks : album.tracks.data
            })
        });
    }
    renderAlbum = () => {
            const {albums} = this.state;
            return albums && albums.length ?
                albums.map((item,index) =>(
                    <div key={index} className="col-md-4 mb-2">
                        <div className="card border-danger">
                            <img src={item.album.cover_big} alt="" className="card-img-top"/>
                            <div className="card-body">
                                <span className="text-primary"> {item.artist.name}</span>
                                <div className="card-title">
                                    {item.title}
                                </div>
                            </div>
                            <div className="card-footer">
                                <div className="links">
                                    <Link to={`details/${item.album.id}`} className="link"><i className="fas fa-info text-danger"></i></Link>
                                    <Link href="/#" className="link" ><i className="fas fa-star text-danger"></i></Link>
                                </div>
                            </div>
                        </div>
                    </div>
                ))
            : null; 
        }
    render(){
        console.log(this.state);
        return(
            <div className="">
                details album
            </div>
        )
    }



}


export default DetailsAlbum ;
